INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'cricket-hill-brewing-company', 'Cricket Hill Brewing Company', 'We believe that New Jersey is starting to learn what the rest of the country already knows; that local microbrews are some of the best beers in the world! Finally, curious beer drinkers are stepping out from under the mind-numbing barrage of large brewery advertising and deciding for themselves what they like. Our beers are designed for those curious drinkers. The large breweries use fillers in their beer; Corn or Rice are the most frequently used. Most good beer should be all Barley, in the same way that good hamburgers are all beef. Barley is expensive and the more fillers that are used, the better the profits! Cricket Hill Beers are 100% Barley as are most great beers. Please take the time to taste different local beers. I am certain tha t those who decide not to let television dictate what they drink are ready for a wonderful adventure into the world of Beer!', null,
      '973-276-9415', 'http://www.crickethillbrewery.com/', '24 Kulick Road', '',
      'Fairfield', 'New Jersey', '7004', 'US', '415',
        '2011-12-12T23:57:38.896-0800', true);

INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'crispin-cider', 'Crispin Cider', 'Classically refined, but not styled as a traditional &quot;sweet beer&quot; beer alternative, Crispin ciders are crisp and clean, not sweet and sticky and never sacrifice superior quality refreshment satisfaction for overbearing complexity.<br><br>Always true to the apple, Crispin Hard Ciders naturally fermented in the USA use fresh pressed apple-juice, not from apple juice concentrate, from a premium blend of US West Coast apples, with no added malt, grape wine or spirit alcohol. Crispin&rsquo;s unique USA produced ciders are smoothed with pure apple juice or from natural sugar sources like organic honey or organic maple syrup, and contain no added colorants, sorbate or benzoate preservatives.<br><br>Crispin ciders have 7 different varieta ls - Original, Light and Brut; classic European styled crisp clean ciders, Browns Lane; an imported classic English dry cider, and our line of Artisanal Reserves; Honey Crisp, The Saint and Lansdowne - unfiltered &quot;cloudy&quot; ciders, ground-breaking &quot;craft beer&quot; uniquely American ciders.<br><br>Crispin ciders are all Gluten Free for the wheat-intolerant person.', 2008,
      '612-331-3699', 'http://www.crispincider.com/', '405 Central Ave SE', 'Suite 10',
      'Minneapolis', 'Minnesota', '55414', 'US', '1519',
        '2011-12-12T23:57:39.171-0800', true);

