INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'dg-yuengling-and-son', 'D.G. Yuengling and Son', 'D.G. Yuengling &amp; Son is the oldest operating brewing company in the United States, having been established in 1829, and is one of the largest breweries by volume in the country. It is the second largest American owned brewery after the Boston Beer Company. Its headquarters is in Pottsville, Pennsylvania.<br><br>Yuengling produces 3.6 million barrels annually, which as of 2005, placed it sixth among American commercial breweries. Besides its two Pennsylvania facilities, Yuengling also operates a brewery in Tampa, Florida.<br><br>The family-owned brewery has traditionally changed ownership through the purchase of the company by the offspring of the previous owner. Due to the popularity of Yuengling Traditional Lager in Pennsylvania, it ca n be ordered by simply asking for a lager, especially in eastern parts of the state.', null,
      '570-622-0153', 'http://www.yuengling.com', '310 Mill Creek Avenue', '',
      'Pottsville', 'Pennsylvania', '17901', 'US', '1388',
        '2011-12-12T23:57:45.116-0800', true);

INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'dl-geary-brewing-company', 'D.L. Geary Brewing Company', 'The D.L. Geary Brewing Company was incorporated in October, 1983 by David and Karen Geary who shared the vision of producing world class products on a small scale for local and regional consumption. At that time, there were only 13 so-called microbreweries in the United States, almost all of them in California and the Pacific Northwest. In the winter of 1984, David Geary began a period of training and research in Scotland and England. With the help of Peter Maxwell Stuart, a Scottish nobleman and brewer who arranged introductions and itineraries, he worked in a half-dozen small commercial breweries from the highlands of Scotland to the south coast of England.', 1983,
      '207-878-2337', 'http://www.gearybrewing.com', '38 Evergreen Drive', '',
      'Portland', 'Maine', '4103', 'US', '423',
        '2011-12-12T23:57:45.403-0800', true);

