INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'diamond-bear-brewing-company', 'Diamond Bear Brewing Company', 'Diamond Bear Brewing Company is Little Rock''s first production brewery in over 15 years. Diamond Bear started brewing on September 21, 2000, and started distribution on October 17, 2000 with the Flying Saucer (Downtown River Market District) being the first establishment to carry our product. We currently distribute our beer throughout Arkansas and Mississippi. Our styles of beer are produced in the old time honored traditional methods of European brewers, using only two-row malted barley, hops, yeast and great Arkansas water. Being brewed locally gives the beer a great advantage in &quot;freshness&quot; while still capturing some of the worlds most famous styles of beer without the use of adjuncts or preservatives!<br><br>You may be wonder ing where our name came from. Diamond Bear is brewed in Arkansas which is the only state to have a diamond mine. This is where the first part of our name came from. As for the bear portion of Diamond Bear, Arkansas was once home to a large population of bears. In fact, during the pioneer days Arkansas was known as the &quot;Bear State.&quot; Therefore, we combined the two to create a name that is distinctly Arkansan, Diamond Bear!', null,
      '501-708-2739', 'http://www.diamondbear.com/', '323C Cross Street', '',
      'Little Rock', 'Arkansas', '72201', 'US', '3224',
        '2011-12-12T23:58:02.791-0800', true);

INSERT INTO organization
