INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'durango-brewing-company', 'Durango Brewing Company', 'Durango Brewing Company is the third oldest microbrewery in Colorado. Brewing since 1990, our heritage dates to 1886 when Durango Beer and Ice Company became Durango Brewing Company. Our beers are hand crafted using the finest barley, hops, and yeast along with water born high in the San Juan Mountains of Southwest Colorado.<br><br>Presently our beers include Durango Amber Ale, Durango Dark Lager, Durango Wheat Beer, and Durango Golden Ale. We also brew Derail Ale, 2007 and 2008 Gold Medal Winner at the Great American Beer Festival (Other Strong Beer Category), and various seasonal beers. Seasonal selections include Blueberry Wheat, Durango Pale Ale, Durango Winter Ale, and 2009 GABF Gold Medal Winner - Colorfest, our fall seasonal.<br><br> In November 2007, we added our new Tap Room which has features inspired by the Narrow Gauge Railroad and the history of our town. The Tap Room is open seven days a week for all to relax and enjoy the Flavor of Durango. Stop by and try a sample or enjoy a fresh pint of any of our award winning beers. Our unique growlers are always available for carry out along with all our packaged products.', 1990,
      '970-247-3396', 'http://www.durangobrewing.com/', '3000 Main Ave', '',
      'Durango', 'Colorado', '81301', 'US', '1584',
        '2011-12-12T23:58:18.895-0800', true);

