INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'elevator-brewing-company', 'Elevator Brewing Company', 'The Elevator Brewer and Draught Haus finds its home in the historic Columbia building, built in 1897 by the Bott family to house the Bott Brother&rsquo;s Billiards and a gentleman&rsquo;s saloon. The beautiful hand-carved, hand-split Philippine mahogany bar was created to serve the whiskey distilled on the second floor. The saloon enjoyed a brisk business from the turn of the century until the advent of prohibition in 1920. The building, rich in history, is on the national registry of historic landmarks and has been restored to much of its original grandeur. Among the original architectural elements that remain today are the beautiful bar, intricate mosaic tile floor, much of the stained glass and of course the decorative ceiling. The luxur ious back-bar won the coveted blue ribbon for craftsmanship at the 1893 World Columbian Exposition in Chicago. Many myths and legends surround this historic building, including the presence of ghosts and spirits. Perhaps during your visit with us, you&rsquo;ll encounter the unexpected.', null,
      '614-679-2337', 'http://www.elevatorbrewing.com/', '161 N. High Street', '',
      'Columbus', 'Ohio', '43215', 'US', '1586',
        '2011-12-12T23:58:30.399-0800', true);

