INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'ellicott-mills-brewing', 'Ellicott Mills Brewing', 'Here at the Mills we have a very simple, yet demanding approach to producing Alpenhof beer. We use the very best ingredients available. We are efficient and precise in production method, yet we allow the product to age to its full maturity. Balance is the key to our beer.  Alpenhof beer is not created to challenge the senses. It is not created to be a complex beer. Well balanced for Alpenhof means smooth and easy-drinking. The way a beer should taste.  To achieve this well balanced taste, only the finest ingredients are used. We import our malt, hops, and yeast from Germany. (For true German style Lager beer, authentic ingredients are essential!) We might spend more for our ingredients, but it is well worth it. This is an expense you can ac tually taste!', null,
      '1-410-313-8141', 'http://www.ellicottmillsbrewing.com', '8308 Main Street', '',
      'Ellicott City', 'Maryland', '21043', 'US', '493',
        '2011-12-12T23:58:33.954-0800', true);

INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'ellicottville-brewing-company', 'Ellicottville Brewing Company', 'To date, EBC is considered one of the finest craft brewers and has received no less than THREE International World Beer Championship Awards.<br><br>The Ellicottville Brewing Company is the result of the desire to open a family business. Inspiration struck during a ski trip to Vail, where other craft brewers began. There was the realization that Ellicottville, sometimes referred to as &quot;the Aspen of the East&quot;, would be the perfect location for a craft brewery &amp; grill.<br><br>After 10 years of building a successfull brewery &amp; restaurant in Ellicottville, our team ventured to open a second location where we could build on our brewing &amp; culinary talents. EBC West opened June 1st, 2005 in the fun-filled college town of Fredo nia, home to SUNY Fredonia.', 1995,
      '716-699-ALES', 'http://www.ellicottvillebrewing.com/', '28A Monroe St', '',
      'Ellicottville', 'New York', '14731', 'US', '1509',
        '2011-12-12T23:58:34.246-0800', true);

