INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'fly-creek-cider-mill', 'Fly Creek Cider Mill', 'The Fly Creek Cider Mill &amp; Orchard is a historic, water-powered mill on the banks of Fly Creek in the heart of beautiful Central New York just minutes from Cooperstown. For more than 150 years visitors have witnessed the pressing of fresh, sweet and delicious cider creating an experience that has become a tradition shared by generations. The Mill Store Marketplace is the heart of activity with sampling of over 40 specialty food products, many made right at the Mill. Daily tastings include apple salsa, zesty salad dressings, hot sauces, preserves, aged cheddar cheese and Mill-made fudge. From the Farm Winery you can taste Hard Ciders and Apple Wines, another feature of the New York cider making tradition.', null,
      '800-505-6455', 'http://flycreekcidermill.com/', '288 Goose Street', '',
      'Fly Creek', 'New York', '13337', 'US', '1852',
