INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'fordham-brewing-company', 'Fordham Brewing Company', 'You have to hand it to Queen Anne; she knew way back in 1703 that you couldn&rsquo;t start a colony without good local brews.  That&rsquo;s why she asked young Benjamin Fordham to start a brewery in the new port of Annapolis. Benjamin&rsquo;s passion and dedication to making the highest quality brews were evident in every pint and the colony flourished!<br><br>Almost 300 years later, Fordham brewing was re-born in the heart of Annapolis in 1995.  With the ever-increasing demand, this frothing Fordham giant was more than our small brewery could handle. In an effort to provide more people with our brews, our plucky staff grabbed our recipes and moved Fordham to its new castle in Dover, Delaware&hellip;a brand new stainless steel castle of opp ortunity!<br><br>Despite geographical changes, our brewery is happy and healthy in Dover, and more productive than ever before. Fordham Brewing remains true to its roots, its recipes and Annapolis heritage.', null,
      '302-678-4810', 'http://fordhambrewing.com/', '1284 McD Drive', '',
      'Dover', 'Delaware', '19901', 'US', '544',
        '2011-12-12T23:59:11.723-0800', true);

