INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'frankenmuth-brewery', 'Frankenmuth Brewery', 'Frankenmuth&rsquo;s founding fathers brought the traditions of brewing to the Mid-Michigan forests in 1845. John Matthias Falliers founded Frankenmuth&rsquo;s first brewery in 1857. Cousins William Knaust and Martin Heubisch opened the Cass River Brewery just north of the Fallier property in 1862, where the Frankenmuth Brewery continues to stand today.<br><br>The Cass River Brewery was purchased by Johann Geyer in 1874, and renamed Geyer&rsquo;s Brothers Brewing Co. For the next 112 years, the facility operated under many names including the Frankenmuth Brewing Co. Ferdinand &ldquo;Fred&rdquo; Schumacher from Duseldorf, Germany, purchased the brewery one year before a fire destroyed most of the structure in 1987.<br><br>The microbrewery con tinued operation following ownership acquisition by Randall E. Heine in 1990. The nation&rsquo;s second oldest microbrewery was at peak production in 1996, distributing more than 30,000 barrels of locally produced microbrews across 25 states, when a F3 tornado struck our facility, causing several million dollars of damage. A brew pub was opened in 2003 and closed three years later.<br><br>Our Frankenmuth Brewing Company, housed in the historic four story, 28,000 square foot building is an exciting destination for food lovers and beer aficionados alike.', 1862,
      '989-262-8300', 'http://frankenmuthbrewery.com/', '425 S Main Street', '',
      'Frankenmuth', 'Michigan', '48734', 'US', '1872',
        '2011-12-12T23:59:19.211-0800', true);

INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'franklins-restaurant-brewery--general-store', 'Franklin''s Restaurant, Brewery & General Store', 'Franklins is located in the heart of historic Hyattsville in a landmark building built in the 1880&rsquo;s as a blacksmith and carriage shop. Starting in 1910, the building housed the Hyattsville Hardware Co. which stayed in continuous operation until 1992. In its heyday, the hardware Co. was the center of community activity, selling everything from pots and pans to remedies for livestock maladies, chicken feed and of course, nuts and bolts. Many people bought their first washing machines and iceboxes here. Trains from Baltimore stopped right out back and delivered loads of lumber, stacks of seed, even whole houses from the Sears catalog. Neighbors gathered and talked about the weather, the schools, rising prices and changing times', 1910,
      '301-927-2740', 'http://www.franklinsbrewery.com/', '5123 Baltimore Avenue', '',
      'Hyattsville', 'Maryland', '20781', 'US', '1612',
        '2011-12-12T23:59:19.497-0800', true);

INSERT INTO organization
