INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'freetail-brewing-company', 'Freetail Brewing Company', '#1 rated brewpub in Texas (rated by BeerAdvocate.com) located at NW Military Highway and Loop 1604. Winner of the San Antonio Express-News 2009 Critics Choice and 2010 Silver Medalist for &quot;Best Place for a Cold Beer&quot; &amp; San Antonio Magazine''s Best of the City &quot;Best Place for a Beer&quot; for two years running (2009, 2010).  Winner of Draft Magazine &quot;Top 25 Beers of 2010&quot; in the smoked beer category for &quot;Rubio Fumando&quot;.<br><br>Named after the official Texas flying mammal, the Mexican Free-tail Bat, Freetail opened its doors Thanksgiving Weekend 2008 and has been cranking out highly rated, fresh, off-the-wall brews ever since.<br><br>We feature fresh craft brews from in house and around the state of Texas  in addition to artisan pizzas, sandwiches, salads, appetizers and desserts made from the highest quality ingredients.<br><br>Join the friendliest staff in town for happy hour M 4-7 andW-F 3-7 with $5 apps and $1 off beer and wine!  Or try us on Tuesdays for Pint Night where Freetail pints are $2.50 all day, or Mondays for $10 pitchers.  Enjoy Freetail brews and guest beer from microbreweries around the state in our spacious open seating, or large outdoor patio with bar service, and catch the game on one of our nine high-definition TV''s and two 100&quot;+ screens.free_wi_fi_spot_copy.gif  Free WiFi for customers.', 2009,
      '210-395-4974', 'http://www.freetailbrewing.com/', '4035 N Loop 1604', 'W #105',
      'San Antonio', 'Texas', '78257', 'US', '1613',
        '2011-12-12T23:59:23.579-0800', true);

