INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'golden-avalanche-brewing-company', 'Golden Avalanche Brewing Company', 'The Golden Avalanche Brewing Company family of beers are brewed according to the German Purity Law of 1516, the Reinheitsgebot. This ancient edict is still observed today in Bavaria and requires beer to be brewed naturally with only water, malted grain, and hops. The three distinct yeast strains our brewmaster selected are all obtained from German sources, and ensure our unique fresh flavor and distinct character. No added preservatives, pasteurization, artificial colors or flavors are ever used in our production process. Even our bubbles are natural in our beers, using the carbon dioxide from fermentation to pressurize the beer.', null,
      '610-683-9600', 'http://kutztowntavern.com/golden.html', '272 W. Main St.', '',
      'Kutztown', 'Pennsylvania', '19530', 'US', '1865',
        '2011-12-12T23:59:43.234-0800', true);

