INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'golden-hills-brewing-company', 'Golden Hills Brewing Company', 'We don&rsquo;t think there&rsquo;s another beer like this in the market place,&rdquo; says Duenwald. &ldquo;Our beers don&rsquo;t fit any category.&rdquo;<br><br>&ldquo;Standard brewing philosophy is if you make a beer with a lot of body, it ends sweet,&rdquo; says Brown. &ldquo;And if you make a beer that&rsquo;s crisp, you can&rsquo;t get body. I came up with a process that allows me to get a rather large amount of body, equivalent to the body of an IPA, but with the finish of a light beer.&rdquo;<br><br>&ldquo;We&rsquo;re bringing beer to the market that ale drinkers will enjoy, because they&rsquo;re going to be different and flavorful, and we&rsquo;ll grab some of the mainstream drinkers. Take my son,&rdquo; Duenwald says. &ldquo;He&rsq uo;s in his 30s, living in Seattle. He&rsquo;ll start the night out drinking a Mac &amp; Jack&rsquo;s or a Mirror Pond. He might even have two of them. But when they&rsquo;re having one of their all-night fun sessions, they&rsquo;ll switch to drinking Bud Light. We make a beer that has the flavor but you can drink it all night.&rdquo;', 1990,
      '509-244-2536', 'http://www.goldenhillsbrewing.com/', 'PO Box 457', '',
      'Reardan', 'Washington', '99029', 'US', '1618',
        '2011-12-12T23:59:44.054-0800', true);

