INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'golden-valley-brewery', 'Golden Valley Brewery', 'Best Brew Pub in the Heart of Wine Country!<br><br>Since we started in 1993, we''ve followed a fresh, local, made-in-house cuisine program at Golden Valley.<br><br>We''ve always had our own kitchen garden supplying the restaurant, and sourced fresh vegetables, fruits, meat, fish and poultry from local producers whenever possible.<br><br>Our chef Greg Meixner orchestrates the procurement of fresh, healthy, great tasting foods for you and your family.<br><br>Everyone knows the rich, fresh and natural taste of our beef but few realize how we do it.<br><br>Since 2003, GVB owners Peter &amp; Celia Kircher have also owned and operated the Angus Springs Ranch on 76 acres of rolling spring fed pastures outside McMinnville.<br><br>Golden Valley Brewer y grain makes ideal feed for our select Black Angus herd because it contains twice the protein and fiber of regular feed. <br><br>Our Angus are raised on an all natural program of spring fed pastures and premium rations for up to 12 months and then dry aged for 21 days insuring beef of exceptional quality and tenderness.<br><br>No hormones, antibiotic feeds, or animal by products are used in the production of our beef. <br><br>Our goal is to produce the finest all natural beef in the Northwest!', 1993,
      '503-472-2739', 'http://www.goldenvalleybrewery.com/', '980 Northeast 4th Street', '',
      'McMinnville', 'Oregon', '97128', 'US', '584',
        '2011-12-12T23:59:45.471-0800', true);

