INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'great-lakes-brewing-company', 'Great Lakes Brewing Company', 'In the 1870s, Cleveland had 30 breweries. By the early 1980s, the last one had shuttered its doors. So when brothers Patrick and Daniel Conway opened Great Lakes Brewing Company on September 6, 1988, it not only signaled a new era in Cleveland brewing, it was also the first microbrewery in the state of Ohio. And the idea of a craft brewery in Cleveland caught on fast, as curious patrons hurried in for a pint crafted in the styles of old and drawn from the taps of the beautiful Victorian era bar. The company''s commitment to sustainable business practices greatly contributed to its early popularity and success. Before long, Great Lakes Brewing Company had become one of Cleveland''s most popular destinations for both dining and a fun night out.', 1988,
      '216-771-4404', 'http://www.greatlakesbrewing.com/', '2516 Market Avenue', '',
      'Cleveland', 'Ohio', '44113', 'US', '605',
        '2011-12-12T23:59:57.823-0800', true);

INSERT INTO organization
