INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'harpoon-brewery', 'Harpoon Brewery', 'We started the Harpoon Brewery in 1986 because, like today, we loved beer and wanted more good choices.  While traveling in Europe after college, we drank many wonderfully diverse, fresh, local beers.  We also saw firsthand how important local breweries were to their communities.  After our travels, we asked each other, why not bring great beer and that sense of community to New England?  That was the genesis of the Harpoon Brewery.  Twenty years later we still revel in making great beer and sharing that joy with our friends and neighbors.  The success that we have had running Harpoon is due entirely to the wonderful employees who brew, package, warehouse, sell, deliver, and market our beer and you, the people who drink Harpoon.  We hope th at our sense of gratitude is reflected in both the quality of the beer and the spirit of fun and enjoyment surrounding our beer and breweries.', 1986,
      '1-888-HARPOON', 'http://www.harpoonbrewery.com/', '306 Northern Avenue', '',
      'Boston', 'Massachusetts', '5089', 'US', '635',
        '2011-12-13T00:00:20.672-0800', true);

INSERT INTO organization
