INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'high-desert-brewing-co', 'High Desert Brewing Co.', 'After years of recipe development, remodeling, and parking lot paving, the High Desert Brewing Co. opened it''s doors in July of 1997 and has quickly become one of New Mexico''s premier brewpubs. Our selection of hand crafted lagers and ales are produced on premises using the finest grains and hops. We brew small batches of about 100 gallons, ensuring that each beer is served at the peak of freshness. Our brews are available by the glass, pint, or pitcher and to-go in 1/2 gallon growlers and 5 gallon kegs. We make every attempt to keep all of our beers on tap at all times but once in a while one or two may not be available for a few days. These times are the perfect opportunity to stretch your taste and try something other than your favorite.  Please let Bob or Donna, or any of the High Desert Brewing Co. staff know how you think we are doing.', null,
      '(575) 525-6752', 'http://www.highdesertbrewingco.com/', '1201 W. Hadley Ave.', '',
      'Las Cruces', 'New Mexico', '88005', 'US', '655',
        '2011-12-13T00:00:38.539-0800', true);

INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'high-falls-brewing-company', 'High Falls Brewing Company', 'High Falls Brewing Company, located in Rochester, New York, is the fifth largest brewing company in America, and the brewers of JW Dundee''s Honey Brown Lager, Michael Shea''s Irish Amber, Genny Light, the Genesee Family of brands and Koch''s Golden Anniversary. High Falls distributes its fine products across 41 states and Ontario, Canada.<br><br>High Falls also brews beer for other beer companies, such as the Boston Brewing Company (makers of Sam Adams beer). As much as 50% of the output of High Falls'' brewery is for other firms. High Falls also distributes beer for other companies, some as far away as New Zealand, which may explain why trucks bearing the brands of other breweries (such as Coors) are sometimes seen on their premises.', 2000,
      '585-263-9446', 'http://www.geneseebeer.com/', '445 St.Paul Street', '',
      'Rochester', 'New York', '14605', 'US', '656',
        '2011-12-13T00:00:38.828-0800', true);

INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'high--mighty-beer-company', 'High & Mighty Beer Company', 'High &amp; Mighty Beer Company began in 2006 as an offshoot of Shelton Brothers beer importing company. Will Shelton took his idea for a novel German-style ale to Paper City Brewery in Holyoke, Massachusetts, and Beer of the Gods was born, to extraordinary commercial success, critical acclaim, and unabashed adoration from beer geeks the world over.<br><br>Eventually, Will discovered that he enjoyed selling the beer he made even more than he did the splendid beers that Shelton Brothers imports, and in June 2008, he abandoned his brother to focus on making American beer that added an American attitude to the best of European beer styles. The beer is still lovingly made at Paper City, but look for High &amp; Mighty to open a brewery of its own  in 2010.', 2006,
      '413-323-8040', 'http://www.highandmightybeer.com/', '108 Cabot Street', '',
      'Holyoke', 'Massachusetts', '01040', 'US', '1723',
        '2011-12-13T00:00:38.251-0800', true);

INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'high-noon-saloon--brewery', 'High Noon Saloon & Brewery', 'During the late 1800&rsquo;s our building housed the Great Western Manufacturing Company which produced cast iron stoves for pioneers heading west. Today, the High Noon Saloon &amp; Brewery is a Leavenworth landmark, serving honest food, fresh made beer, quality entertainment and a whole lot of FUN!<br><br>We have a lot going on! We&rsquo;re a big, family-friendly restaurant featuring Steaks, Burgers, Sandwiches, Entr&eacute;e Salads, BBQ, Wild Salmon (flown in FRESH not frozen), Buffalo Burgers (locally raised, hormone &amp; anti-biotic free) and so much more. We also feature daily Lunch, Dinner and Drink Specials!<br><br>We are also Leavenworth&rsquo;s hometown brewery, serving fresh made, award-winning hand-crafted beer from our on-site  copper clad brewery. Our silver medal-winning Annie&rsquo;s Amber Ale is available throughout Kansas in 6-pack 12oz. cans! The Kansas Department of Commerce has recently awarded High Noon &amp; Annie&rsquo;s Amber Ale the &ldquo;Simply Kansas&rdquo; label as a quality product made exclusively in Kansas!', null,
      '913-682-4876', 'http://www.thehighnoon.com/', '206 Choctaw St', '',
      'Leavenworth', 'Kansas', '66048', 'US', '1624',
        '2011-12-13T00:00:39.112-0800', true);

