INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'hoppin-frog-brewery', 'Hoppin'' Frog Brewery', 'Hoppin&rsquo; Frog is a small, hands-on brewery making very flavorful beers in the most flavorful beer styles. We brew high quality beers with an uncompromising focus on detail, from the ingredient selection to the brewing process. A specially designed JV Northwest brewing system creates more flavorful beers by allowing more barley to be used in the brewing process.  We also use a special bottling machine that keeps the beer fresher, and extends storage times compared to other conventional microbrewery bottling machines. This is very beneficial to retain all the character of these flavorful styles of beer.<br><br>Hoppin&rsquo; Frog is the concept of owner and established brewmaster Fred Karm, who has designed and produced more award-winning beers than any other professional brewer in Ohio since 2000. All of these awards were won at the 2 most prestigious contests in the world, including the Great American Beer Festival and the World Beer Cup. Mr. Karm has been a brewer of specialty beers since 1994, a life-long Akron, Ohio resident, and is a graduate of the University Of Akron.', 1994,
      '330-352-4578', 'http://www.hoppinfrog.com/', '1680-F East Waterloo Rd.', '',
      'Akron', 'Ohio', '44306', 'US', '673',
