INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'jailhouse-brewing-company', 'Jailhouse Brewing Company', 'Some often tell me that the old Hampton Jailhouse was the overnight home to many a drunken guest through the years. It seems only fitting that beer is being brewed in this quaint little two-story brick structure built in the 1920''s. It''s had its history as the jail, fire station, courthouse, mason lodge, and last but not least a sandwich shop that made those little sandwiches you can buy in gas stations. You know the triangle packaged ones that have mystery meat in them? Don''t lie, you''ve had one. Today it is home to JailHouse Brewing Company and we are brewing hand-crafted beer not punching out license plates.', 2008,
      '678-734-3202', 'http://www.jailhousebrewing.com/', '8 Cherry St', '',
      'Hampton', 'Georgia', '30228', 'US', '1631',
