INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'kern-river-brewing-company', 'Kern River Brewing Company', 'The Kern River Brewing Company (KRBC) brings people back to the magnificent simplicities that life has to offer.<br><br>From its beginnings, Kernville has been a wild-west town.  Established as a mining settlement in the mid 1800''s and continuing as a ranching community through the 1900''s, Kernville remains a town of adventure, offering a wide variety of outdoor activities.  On any given day, the outdoor enthusiast enjoys activities ranging from the excitement of whitewater rafting, kayaking, downhill skiing, cross-country skiing, snowmobiling, horseback riding, attending rodeos, windsurfing, water-skiing, mountain biking and climbing.  If a more relaxing day is preferred, people can enjoy fishing, hiking, camping, sailing, antiquing, and b ird watching or simply kicking back and taking in the spectacular views of the Sierra Nevada mountain range.', 2006,
      '760-376-2337', 'http://www.kernriverbrewingcompany.com/', '13415 Sierra Highway', '',
      'Kernville', 'California', '93238', 'US', '1421',
        '2011-12-13T00:01:43.897-0800', true);

