INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'kettlehouse-brewing-company', 'Kettlehouse Brewing Company', 'It&rsquo;s 1991. Tim O&rsquo;Leary, a third generation Montanan was living in Colorado, finding his way working for NOAA, mountain biking and skiing&hellip; and feeling a little bit like a fish out of water. Colorado is a beautiful state, but Montana was home. One afternoon on his bike ride home, he spotted a new business that had cropped up in Boulder. It was a Brew on Premise (BOP) &ndash; a place where customers could come in and brew their own beer. Having dabbled in homebrewing for a few years, he tried it and thought, &ldquo;This is my ticket back home.&rdquo;<br><br>Fast forward to 1995. Tim and his wife, Suzy Rizza, open Missoula&rsquo;s first BOP. Lots of people came to try it, and lots of people had a great time doing it. It wasn& rsquo;t quite making the bills though, and for the next 13 years, through good times and worse times - Tim was the guiding force behind the transformation of the Kettlehouse into a small microbrewery and neighborhood taproom.', 1991,
      '406-728-1660', 'http://www.kettlehouse.com/', '602 Myrtle St.', '',
      'Missoula', 'Montana', '59801', 'US', '1633',
        '2011-12-13T00:01:44.455-0800', true);

INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'keuka-brewing-company', 'Keuka Brewing Company', 'Keuka Brewing Co., LLC opened its doors to the public in the fall of 2008 is overlooking beautiful Keuka Lake, the brewery&rsquo;s namesake, in the heart of the Finger Lakes. It is the first Microbrewery on this breathtaking lake and the only microbrewery on the Keuka Lake wine trail. Surrounded by some of New York State&rsquo;s best wineries, Keuka Brewing offers an alternate world-class epicurean experience for wary wine drinkers and beer connoisseurs alike. <br><br>Keuka Brewing is owned and operated by Linda &amp; Richard Musso. Bringing years of home brewing experience to the business, Keuka Brewing uses only premium ingredients of malted barley, wheat, and a variety of hops to produce a wide array of top grade hand crafted ales and de licious beers to this unique region.', null,
      '607-868-4648', 'http://keukabrewingcompany.com/', '8572 Briglin Rd', '',
      'Hammondsport', 'New York', '14840', 'US', '1859',
        '2011-12-13T00:01:44.743-0800', true);

INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'keweenaw-brewing-company', 'Keweenaw Brewing Company', 'Keweenaw Brewing Company is the hops-induced vision of Dick Gray and Paul Boissevain. Dick, a Michigan Tech graduate was looking for a way to return to the Copper Country. Paul, meanwhile, was introduced to - and seduced by - the crystal clear waters of nearby Lake Superior, the beauty of the surrounding area, and the small-town charm of Houghton, Michigan. The two men, whose passion for beer runs as deep as Superior, are the driving force behind Keweenaw Brewing Company.', 2001,
      '906-482-5596', 'http://www.keweenawbrewing.com/', '408 Shelden Avenue', '',
      'Houghton', 'Michigan', '49931', 'US', '1788',
        '2011-12-13T00:01:45.021-0800', true);

INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'kichesippi-beer-company', 'Kichesippi Beer Company', 'Kichesippi Beer is a family owned business, brewing in Ottawa, Ontario.<br><br>The question we get asked the most is; a Cool name, what does it mean?Kichesippi is a word meaning &quot;The Great River&quot;. Prior to 1855, when the City of Ottawa was founded, the Ottawa River was known as the Kichesippi. The river has consistently been an integral part of the history of Ottawa and Eastern Ontario.<br><br>We have been involved with the beer industry since 1995 in many different capacities. Working with breweries in Halifax, Toronto and Guelph introduced us to array of beer from around the world. Everything from British Columbia craft beer, to brands from Belgium, Germany and Japan; have fueled our passion for great beer.', 2009,
      '866-361-4477', 'http://www.kbeer.ca/', '866 Campbell Avenue', '',
      'Ottawa', 'Ontario', 'K2A 2C5', 'CA', '1739',
        '2011-12-13T00:01:45.299-0800', true);

