INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'la-brasserie--vapeur', 'La Brasserie  Vapeur', 'This Belgian farmhouse brewery is located in the middle of Pipaix, in the Wallonian Hainut region (15 km from Tournai, 74 km from Brussels). This family-owned brewery is named after the steam engines which power the brewing process. Their saison (seasonal) beers reflect the Wallonian brewing tradition, as there are many spices in the beers, although all their beers are spiced. They hold public brewing sessions from 9-12 noon, the last Saturday of each month.  Call ahead to reserve a spot; tours are in French and English. They serve a grand lunch buffet along with their beers in their brasserie across the street (30 &euro; per person). Check the website or call for more information. <br>La Brasserie &agrave; Vapeur brews these beers: Saison  de Pipaix (saison), Vapeur en Folie (lager), Vapeur Legere (lager), Vapeur Cochonne (amber-style).', 1984,
      '+32 (069) 66 20 47', 'http://www.vapeur.com/vap0E.html', '1 rue du Marchal ', '',
      'PIPAIX (Leuze-Belgique)', '', '7904', 'BE', '1773',
        '2011-12-13T00:01:58.284-0800', true);

INSERT INTO organization
