INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'laurelwood-brewing-company', 'Laurelwood Brewing Company', 'Hi, my name is Mike De Kalb, and I&rsquo;m the co-owner of Laurelwood Public House &amp; Brewery in Portland, Oregon. My wife Cathy and I started Laurelwood in 2001 because we wanted a place where family and friends could meet in a fun and relaxed atmosphere. My restaurants are non-smoking and family-friendly.<br><br>And the beer? World class. Laurelwood was the first brewery in Portland, Oregon to brew beers that met the rigorous certified organic standards of Oregon Tilth. We&rsquo;ve garnered lots of awards, including Champion Brewery and Brewmaster at the 2004 World Beer Cup.<br><br>Stop by one of our three public houses and or one of our two Portland  airport locations for a pint and a meal.   Make sure when you are out  and about that  you ask for Laurelwood beer at your favorite Oregon or Washington pub.', 2001,
      '503-282-0622', 'http://www.laurelwoodbrewpub.com/', '5115 Ne Sandy Blvd', '',
      'Portland', 'Oregon', '97213', 'US', '1637',
        '2011-12-13T00:02:12.001-0800', true);

INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'lavery-brewing-company', 'Lavery Brewing Company', 'Jason Lavery began his brewing career as a obsessive homebrewer (still home-brewing today with his 3 young children). After winning a ''People Choice Award'' for his Smoked Porter in March 2009, it was suggested they try to make their brewing dream a reality. Small business classes were attended, banks were met with and Jason began a apprenticeship at Brewerie, an Erie brewpub.<br><br>While communicating with the Brewer''s Association in June 2009, the idea was presented that Lavery Brewing Co. might consider seeking a &lsquo;Alternating Proprietorship&rdquo; license. This classification of license means a brewing facility alternates proprietors. A deal was struck with Brewerie to use their equipment on the weekends and a new brewery was born. Currently, Lavery Brewing Co. is looking for its own space and equipment with intentions to open in Spring 2011.<br><br>We are not a contract brewery! We rent equipment form Brewerie but rest assured we brew, look after, condition, keg and bottle these beers. A brewer is not his kettle; a brew kettle is just a blank canvas waiting for an artist.<br><br>Location Update: We''ve settled on a space so the next few months should be a whirlwind. We''ve already met with contractors, plumbers, electricians and equipment sellers.', 2009,
      '814-602-5845', 'http://www.LaveryBrewing.com/', '123 W 14th St', '',
      'Erie', 'Pennsylvania', '16501', 'US', '1480',
        '2011-12-13T00:02:12.291-0800', true);

INSERT INTO organization
