INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'left-hand-brewing-company', 'Left Hand Brewing Company', 'Left Hand began in December 1990 with a homebrewers kit founder Dick Doore received from his brother. According to Dick, &quot;it was all downhill from there.&quot; By 1993, Dick had teamed up with college buddy Eric Wallace and they resolved to start a brewery.<br><br>In September 1993, they incorporated as Indian Peaks Brewing Company, and purchased a former meat-packing plant next to the St. Vrain river outside downtown Longmont, CO. A few weeks after beginning production, it was discovered that the name Indian Peaks was already in use by another Brewery, so the name was changed to Left Hand, in honor of Chief Niwot (the Arapahoe word for &quot;left hand&quot;) whose tribe wintered in the local area.<br><br>Left Hand''s doors opened for b usiness on January 22, 1994. Their first batch of beer was Sawtooth Ale, which has since become their most popular brew. In October of that year, Left Hand took home two medals at the Great American Beer Festival, a Gold Medal in the bitter category for Sawtooth Ale, and a Bronze Medal in the Robust Porter category for Black Jack Porter.', 1993,
      '1-303-772-0258', 'http://www.lefthandbrewing.com/', '1265 Boston Avenue', '',
      'Longmont', 'Colorado', '80501', 'US', '779',
        '2011-12-13T00:02:15.425-0800', true);

INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'leftys-brewing-company', 'Lefty''s Brewing Company', 'Lefty''s Brewing Company is owned and operated by Bill &ldquo;Lefty&rdquo; Goldfarb in Bernardston, MA. This nano-brewery is a home brewer''s dream come true. Bill, who has been a roofer by day and a home brewer by night for almost a decade, has finally decided to provide his popularly sought-after product to the public. He has traveled as far as Wisconsin to obtain equipment for his two barrel brewhouse. He is excited to finally put down the hammer and dedicate his full attention to commercially brewing his fine-tuned recipes. Bill, who firmly believes a local, family business offers the best service and product, is proud to have a family-run business operated by only him and his girlfriend. His product is hand-bottled and labeled. As of now , the beer is available in Franklin County, MA, with a wider distribution area in the future. Lefty creates high quality beers using only the finest quality ingredients available. He focuses on the simple pleasures of the traditional and time-tested European brewing techniques. Lefty looks forward to sharing 6 year-round beers, with seasonal and specialty batches offered throughout the year.', null,
      '413-648-6111', 'http://leftysbrew.com/', '3 South St.', '',
      'Bernardston', 'Massachusetts', '01337', 'US', '1860',
        '2011-12-13T00:02:15.712-0800', true);

