INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'morgan-street-brewery', 'Morgan Street Brewery', 'Nestled on cobblestone streets polished by more than 200 years of commerce along the Mississippi River, Morgan Street Brewery radiates the charm that only history can impart.  Morgan Street Brewery&rsquo;s rich architecture and warm interiors are highlighted by burnished wood and exposed brick and feature cozy nooks and corners.  The main brewery building and adjacent Lodge, both more than 130 years old, are separated by a brick patio that is as perfect for caf&eacute; style dining as the popular outdoor dining patio along second street.<br><br>Morgan Street Brewery, founded in 1995, is one of the oldest buildings in historic Laclede&rsquo;s Landing.  It&rsquo;s where St. Louis- and thousands of visitors from other cities- go for great time s, great food and fabulous beers made right on the premises in Morgan Street&rsquo;s micro brewery.', null,
      '314-231-9970', 'http://www.morganstreetbrewery.com/', '721 North 2nd Street', '',
      'St. Louis', 'Missouri', '63102', 'US', '1981',
        '2011-12-13T00:03:39.430-0800', true);

