INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'olde-peninsula-brewpub-and-restaurant', 'Olde Peninsula Brewpub and Restaurant', 'Olde Peninsula is a micro brewery and a full service pub and restaurant.      ? ?<br>Kalamazoo&rsquo;s first brewpub, opened to the public on St. Patrick&rsquo;s Day in 1996. Previously known as the &ldquo;Bib Corner&rdquo;, this location has an interesting historical past. Originally designed for the Peninsula restaurant, this three story structure was built by Nicholas Bauman in 1874.<br><br>This elegant restaurant gave way to the Franklin and Folz clothing store from 1884 to 1924. Through the years, the building housed a succession of businesses, including the Kalamazoo Stove company, a book bindery and a hotel.<br><br>It became the headquarters for W.R. Biggs/Gilmore Agency in 1973. Severely damaged by the 1980 tornado, the building was  completely rehabilitated in 1980-81.<br><br>The current owners, Marie and Stephen Blinn, wish to bring back a piece of history for all of Kalamazoo to enjoy. We are proud to offer to you the freshest beer available, brewed on-site in our 7-barrel brew house.', 1996,
      '1-269-343-2739', 'http://oldepenkazoo.com/', '200 East Michigan Avenue', '',
      'Kalamazoo', 'Michigan', '49007', 'US', '943',
        '2011-12-13T00:04:44.647-0800', true);

INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'olde-saratoga-brewing-company', 'Olde Saratoga Brewing Company', 'Located in historical Saratoga Springs, New York, the Olde Saratoga Brewing Co. has been a well-kept secret in the city.<br><br>Established in 1997, it is the fourth largest brewery in New York State and produces the highest quality and freshest craft brewed ales and lagers in the area.<br><br>The company is owned by the Mendocino Brewing Company, which is based in California. The Olde Saratoga Brewery brews about 30 different kinds of beer throughout the year - over 20,000 barrels last year. A third of their business is through contract brewing for a variety of breweries throughout the Northeast . Contract beers allow the brewery to make a variety of unique beers from light beer to a white Belgian to dark Stout beer and also gives the publ ic a chance to taste of variety of great beers throughout the year.', 1997,
      '518-581-0492', 'http://www.oldesaratogabrew.com/', '131 Excelsior Avenue', '',
      'Saratoga Springs', 'New York', '12866', 'US', '1914',
        '2011-12-13T00:04:44.949-0800', true);

