INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'olvi', 'Olvi', 'OLVI beers are made from carefully selected and mainly Finnish ingredients. The only exception is the hops which are imported from abroad because the weather conditions in Finland are not conducive to growing sufficiently high quality crops.<br><br>OLVI beers differ from one another in the selection of their raw materials as well as in their manufacturing methods. Olvi allows its beers to mature slowly and gradually, which is the only way as there is no shortcut to success.<br><br>All Olvi''s beers are lager beers, which means that they are produced with bottom-fermenting yeast. Lager beers are either dark or light. The bottom-fermenting yeast ferments the beers in a cool temperature, which produces a very fresh, crisp taste and aroma. All O lvi beers are unpasteurized.', 1878,
      '358 290 00 1050', 'http://www.olvi.fi/', 'P.O. Box 16', '',
