INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'paulaner', 'Paulaner', 'The name of the Paulaner brewery refers to the order of friars that resided in Neuhauser Stra&szlig;e in Munich who were part of the order of Saint Francis of Paola. The monks had brewed beer for their own use since 1634. The beer that was permitted to be sold on holidays was a Bock style which gained local fame. After the abolition of the Neudeck Cloister in 1799, the building was converted into a penitentiary. Franz Xaver Zacherl, the brewer, purchased the former cloister brewery and continued the Starkbier tradition with the product Salvator, which is latin for Saviour. In 1861 the Salvatorkeller (Salvator cellar) was opened upon Nockherberg. In 1928 the brewery merged with the Gebr&uuml;der Thomas brewery creating Paulaner Salvator Thom as Br&auml;u. 1994 saw the acquisition into the Kulmbacher brewery group with the affiliated producers Plauen and Chemnitz. A daughter company is the Auer Br&auml;u AG Rosenheim. Paulaner belongs to the BHI (Brau Holding International AG), a joint venture between Sch&ouml;rghuber Ventures (50.1%) and with Holland''s Heineken N.V. (49.9%).', 1634,
      '', 'http://www.paulaner.com/', '', '',
      'Munich', '', '', 'DE', '972',
        '2011-12-13T00:05:05.241-0800', true);

