INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'pizza-port-brewing--solana-beach', 'Pizza Port Brewing - Solana Beach', 'Pizza Port was established in March of 1987 when we started making pizzas in our present location in Solana Beach! We started brewing beer as a hobby and as we began brewing more than we could leisurely drink, we really wanted to share some of the incredible brews that we created for fun with our customers too. We installed a 7 barrel brewery and served our first handcrafted beer brewed on the premises in October of 1992. In July of 1997, we celebrated our 10th anniversary by opening another location in Carlsbad. We expanded our brewing capacity into the building next door creating the opportunity to brew, bottle, distribute, and drink even more. Our distribution center is called PORT BREWING!  We found the perfect location in San Clemente  and opened in July of 2003 enabling us to brew several additional styles! Let''s party! When people started asking where they could get our beer besides Pizza Port enough, we acquired the old Stone Brewing facility in San Marcos at 155 Mata Way #104 to expand our distribution as PORT BREWING! We officially opened on Cinco de Mayo 2006. Now we will be able to make up to 10,000 barrels to get out to your other favorite places to enjoy coldies! More impressive is the 100 plus barrel room for all of our barrel aged specialties. Come check out our tasting bar where you can get bottles, jugs &amp; merchandise to go while you sample what''s brewing!', null,
      '1-858-481-7332', 'http://www.pizzaport.com', '135 North Highway 101', '',
      'Solana Beach', 'California', '92075', 'US', '1001',
        '2011-12-13T00:05:27.544-0800', true);

