INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'thirsty-bear-brewing-company', 'Thirsty Bear Brewing Company', 'Since my first home brew many years ago, I&rsquo;d been dreaming of opening a brewery. I had no idea if that dream would come true or if it did what I&rsquo;d call it, but I was open to naming my dream. One summer Sunday in 1991 I was reading the San Francisco Chronicle. A head line in the front section blazed into my brain: Thirsty Bear Bites Man for Cold Beer. After reading about the Bear biting Victor Kozlov&rsquo;s hand to get a cold beer, I knew that story would be the name of my dream. The article, neatly cut out, lived on my refrigerator door for the next five years until I gave it greater purpose by opening ThirstyBear Brewing Company. You are looking at the original cut out article. Victor Kozlov&rsquo;s sacrifice of his hard earne d beer to the Bear has earned him immortal status in our house beer, Kozlov Stout.', null,
      '415-974-0905', 'http://www.thirstybear.com/', '661 Howard Street', '',
      'San Francisco', 'California', '94105', 'US', '1971',
        '2011-12-13T00:08:47.704-0800', true);

INSERT INTO organization
