INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'tuckahoe-brewing-co', 'Tuckahoe Brewing Co.', 'Tuckahoe Brewing Company was established in January of 2011 by four friends who had been brewing beer for fun for several years. We were brewing five and ten gallon batches while we barbequed and enjoyed drinking beer with family and friends. Going to the next level and brewing commercially was a thought that was always in the back of each our minds, but after we heard enough of our friends say ...the same thing, we decided to take the idea seriously. <br><br>We expect to begin with four recipes, a Belgian Wit, an English Style Porter, a Pale Ale. Cape May County is a beautiful place, and brewing a beer that captures that beauty in both its ingredients and the finished product is one of our goals. We want to provide the people of Cape May C ounty beers that will always remind them of home.<br><br>Please look for our beers on tap in bars and restaurants around Cape May County, and bottled for take home as well. Also, please stop by and see us this fall at 9 Stoney Road in Dennis Township.', null,
      '609-827-5375', 'http://tuckahoebrewingcompany.blogspot.com/', '6 Prosit Lane', '',
      'Ocean View', 'New Jersey', '08230', 'US', '2850',
        '2011-12-13T00:09:15.542-0800', true);

