INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'tuppers-hop-pocket-brewing-company', 'Tuppers'' Hop Pocket Brewing Company', 'An interest in history and beer led Bob and Ellie Tupper to  a quest throughout the United States and Europe to talk to brewers and sample classic examples of traditional brewing styles.  In 1978 they began taking formal tasting notes on each new beer they could find.<br><br>By the early 1990s the Tuppers had tasted and evaluated over 6,000 beers and visited hundreds of breweries.  They had found many excellent beers, but envisioned an ale that no one had made&mdash;one that would encompass bold American flavors and traditional European brewing techniques.  They asked Old Dominion Brewing Co., which they felt was one of the very best craft breweries in the country, to work with them to develop this ideal ale.  Tuppers&rsquo; Hop Pocket Ale  was the result.  <br><br>In 1997 the Tuppers again worked with the Old Dominion brewers to create Tuppers&rsquo; Hop Pocket Pils, based on the Keller or Hefe Pils style of Northern Bavaria, but with a unique American boldness to the flavor.<br><br>The Tuppers live in Bethesda, Maryland, where Bob teaches history.  Ellie works in Washington as a production editor of scientific books.  The Tuppers still travel and taste beers and now have notes on over 14,000 different brews.', null,
      '301-229-2027', 'http://tuppersbeers.com/', '6404 Redwing Road', '',
      'Bethesda', 'Maryland', '20817', 'US', '1293',
        '2011-12-13T00:09:17.921-0800', true);

