INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'upslope-brewing-company', 'Upslope Brewing Company', 'In the State of Colorado, we are blessed, as a brewery, to have the ability to have a wholesale liquor license (in addition to our manufacturers liquor license) which permits us to self-distribute. This means that we can buy a van, knock on doors of independently-owned liquor stores, have them sample what we have to offer, and let them decide to stock their shelves with our products . . . or not. This is opposed to the very common &ldquo;Three Tier&rdquo; system in many states which mandate that there is a brewery, there is a distributor, and there is a retailer. Brewery and retailer never meet.', null,
      '720-379-7528', 'http://www.upslopebrewing.com/', '1501 Lee Hill Rd', '',
      'Boulder', 'Colorado', '80304', 'US', '1703',
        '2011-12-13T00:09:34.048-0800', true);

