INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'wicked-beaver-brewing-company', 'Wicked Beaver Brewing Company', 'Wicked Beaver is a West Texas Craft Brewery, formed in Spring of 2009. We are a hands-on Brewery, with no automation to speak of, this keeps us in touch with every batch of beer we brew.<br>	<br>On an old dirt road, far from the beaten path, deep in the midst of no-where lives a Wicked Beaver. Why is he Wicked, you may ask? He is Wicked from the ways of the world, so many demands, too many deadlines, minor appreciation, and never enough time. So with little more than the stick in his hand and the Hops in his hanker chief, he journeyed West. Upon arriving in the West he hopped from the tailgate, choked on the dust, and kicked a tumble weed rolling his direction. This is the West, the place full of opportunity &ldquo;he asked himself&rdquo; a nd sighed but after a few Wicked Cold months, the landscape began to change, grass became lush green, trees bloomed and the flowers opened. The Wicked Beaver found the warm days, and cool nights were perfect to pursue his passion.<br><br>The Wicked Beaver and his brewing partners have been Brewing Craft Specialty Beer&rsquo;s in secret for years, perfecting the fragrant aroma by blending only best hops and hand selecting the Malted Barley for the most unique varieties of flavors and textures. The Wicked Beaver is ready to share his unique creations with you!', 2009,
      '806-833-6969', 'http://www.wickedbeaverbrewing.com/', '', 'PO Box 942',
      'Wolfforth', 'Texas', '', 'US', '2807',
        '2011-12-13T00:10:08.007-0800', true);

INSERT INTO organization
