INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'yards-brewing-company', 'Yards Brewing Company', 'In 1988, two cash-strapped collegiate wrestlers realized that they could sustain their thirst for quality beer without spending their modest savings at the local liquor store by brewing beer themselves. So began the friendship of Tom Kehoe and Jon Bovit.<br><br>As they made their way through college they continued to experiment with homebrewing and formulating new recipes. Proud of their product, they were eager to share it with friends. An instant favorite, it wasn''t long until they realized their modest homebrewing operation wasn''t going to be enough to satisfy the growing demand.<br><br>During the early 1990s, many breweries opened, and craft brewing started to gain popularity. Working in a small English-style brewery in Western Maryland , Tom Kehoe and Jon Bovit decided to take matters into their own hands, and in 1994, they founded Yards Brewing Company.', 1994,
      '215-634-2600', 'http://www.yardsbrewing.com/', '901 N. Delaware Avenue', '',
      'Philadelphia', 'Pennsylvania', '19123', 'US', '1383',
        '2011-12-13T00:10:27.510-0800', true);

INSERT INTO organization
