INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'cervejaria-way', 'Cervejaria Way', 'A Way Beer &eacute; uma micro cervejaria artesanal do Paran&aacute;, localizada em Pinhais, na regi&atilde;o metropolitana de Curitiba. Com produtos de alta qualidade e um esp&iacute;rito arrojado, em pouco tempo de exist&ecirc;ncia ganhou destaque na regi&atilde;o e no pa&iacute;s. Hoje fabrica cinco cervejas diferentes  American Pale Ale, Amburana Lager, Cream Porter, American Lager e Irish Red Ale  e distribui para pessoas f&iacute;sicas e estabelecimentos comerciais da capital paranaense. N&oacute;s da Way Beer fazemos cervejas de alto padr&atilde;o. Primamos pela qualidade e acessibilidade dos nossos produtos a diferentes tipos de p&uacute;blico. S&atilde;o receitas exclusivas, desenvolvidas na pr&oacute;pria cervejaria, para agradar  os mais diversos paladares. Acreditamos que um bom produto &eacute; a melhor forma de divulgar a cultura cervejeira. Al&eacute;m disso, procuramos estar constantemente inovando, mas sempre mantendo o padr&atilde;o de qualidade e com a m&aacute;xima efici&ecirc;ncia. Quando for apreciar, lembre-se: beba menos, beba melhor! A Way Beer estreou oficialmente em novembro de 2010, no Festival Brasileiro da Cerveja, na cidade de Blumenau, em Santa Catarina. De l&aacute; para c&aacute;, a cervejaria conquistou a cr&iacute;tica e o p&uacute;blico do pa&iacute;s pela qualidade de seus produtos. Al&eacute;m dos elogios dos presentes no festival, a Amburana Lager foi destaque nas palavras do escritor e cr&iacute;tico de cervejas canadense Stephen Beaum ont. Em abril de 2011, a Way American Pale Ale foi eleita a melhor Pale Ale no 1&ordm; Pr&ecirc;mio Maxim de Cervejas Brasileiras. E muito mais est&aacute; por vir para construir essa hist&oacute;ria.', 2010,
      '+55 41 36538853', 'http://www.waybeer.com.br', 'Rua P&eacute;rola, 331 - Emiliano Perneta', '',
      'Pinhais', 'Paran&aacute;', '83325-200', 'Brasil', '',
        null, true);

INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'zatec-brewery', 'Zatec Brewery', 'The Zatec brewery - built in 1801 - is situated inside the Zatec castle walls, overlooking the town and hop fields. The brewery suffered for lack of investment during the Communist years until by 2000 the brew was hardly available even locally, much to the detriment of the Zatec economy. In year 2001 Rolf Munding purchased the brewery from a local business man and began a program of investment which has rejuvenated the brewery and improved the prospects for the beer, brewery and the people of Zatec, both as beer drinkers and brewers. Tomas Lejsek, one of Czech Republics leading and most innovative brew masters has masterminded the changes, bringing together new technology, old brewing processes and pure ingredients.', 1800,
