INSERT INTO organization
     (type, slug, name, description, established, phone, website, address, address2, locality, region, postal_code, country, brewery_db_id, brewery_db_last_updated, locked)
     VALUES ('BREWERY', 'great-raft-brewing', 'Great Raft Brewing', 'Great Raft Brewing is a Shreveport, Louisiana based craft brewery dedicated to making creative, authentic beer and helping to build a great beer culture within communities it serves. Great Raft Brewing''s mission is to create fresh craft beer that complements the lifestyles and cultures of Louisiana. Our beer will be complex, inspired, bold and plentiful, providing our customers a beer experience unlike any other. Southern people enjoy spicy, flavorful food and Great Raft beer will not only stand up to the bold flavors in our cooking, but complement it as well. Great Raft beers will be produced with the finest hops and grains, providing our customers with consistently great beer in every batch. Great Raft Brewing was founded on the principle  of brewing what we call Real Beer. Real Beer is brewed with creativity, passion, and authenticity. Our beer is brewed with creativity that pushes the boundaries of style, a passion for greatness, and authentic, quality ingredients. Drink Real BeerTM. You deserve it.', 2013,
      '(318) 734-8101', 'http://www.greatraftbrewing.com', '1251 Dalzell St', '',
